package com.example.controller;import com.example.enity.User;import com.example.repositories.UserRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;@RestController@RequestMapping("/user")public class UserController {    @Autowired    private UserRepository userRepository;    @GetMapping("/hello")    public String hello(){        return "Hello";    }    @GetMapping()    public List<User> getALL(){        return userRepository.findAll();    }    @PostMapping    public User save(User user){        return userRepository.save(user);    }}